import Vue from 'vue';
import Vuex from 'vuex';

Vue.use(Vuex);

const state = {
  username: 'admin',
  password: 'admin',
  isLogin: false,
  loginMessage: false,
};
const getters = {
  getIsLogin: (state) =>{
    return state.isLogin;
  }, 
  getLoginMessage: (state) =>{
    return state.loginMessage;
  }
};
const actions = {};
const mutations = {};

const store = new Vuex.Store({
  state,getters,actions,mutations
});

export default store;
