import Vue from 'vue';
import Vuex from 'vuex';

Vue.use(Vuex);

const state = {};

export default new Vuex.Store({
  state: {
    isLoggedIn: false
  },
  mutations: {
    setLoggedIn(state, value) {
      state.isLoggedIn = value;
    }
  }
});
