import Vue from 'vue';
import Vuex from 'vuex';

Vue.use(Vuex);

const store = new Vuex.Store({
  state: {
    isLoggedIn: false,
    // Diğer state değerleri burada tanımlanabilir
  },
  mutations: {
    setLoggedIn(state, value) {
      state.isLoggedIn = value;
    },
    // Diğer mutations burada tanımlanabilir
  },
  // Diğer Vuex yapılandırmaları burada tanımlanabilir
});

export default store;
